module.exports = function(data){
    if(!(data && data.length > 1)){
        return null
    }else{
        first_line = data.split(/\r\n|\r|\n/)[0];

        if(first_line.match(/\t/)) {
            return {type: "tsv"}
        }else if(first_line.match(/\,/)){
            return {type: "csv"}
        }else if(first_line.match(/^\<.+?\>$/)) {
            return {type: "xml"}
        }else if(first_line.match(/^\{/)){
            return {type: "json"}
        }else{
            return {type: "unknown"}
        }
    }
};